import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form } from "react-bootstrap";
import { matchService } from "../services/matchService";

const Home = () => {
  const [duration, setDuration] = useState(""); // Match duration
  const [gameMode, setGameMode] = useState(""); // Game mode selection
  const navigate = useNavigate();

  const handleCreateMatch = async () => {
    if (!duration || !gameMode) {
      alert("Please enter both match duration and game mode.");
      return;
    }

    try {
      // Send the request to create a match
      const response = await matchService.createMatch({ duration, gameMode });

      // Navigate to the match details page after successful creation
      navigate(`/match/${response.matchId}`);
    } catch (error) {
      console.error("Error creating match:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Create a Time-Limited Match</h1>

      <Form>
        {/* Match Duration Input */}
        <Form.Group controlId="formDuration" className="mb-3">
          <Form.Label>Match Duration (in seconds)</Form.Label>
          <Form.Control
            type="number"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
            placeholder="Enter match duration"
          />
        </Form.Group>

        {/* Game Mode Selection */}
        <Form.Group controlId="formGameMode" className="mb-3">
          <Form.Label>Game Mode</Form.Label>
          <Form.Control
            as="select"
            value={gameMode}
            onChange={(e) => setGameMode(e.target.value)}
          >
            <option value="">Select Game Mode</option>
            <option value="timed">Deathmatch</option>
            <option value="round-based">Capture the Flag</option>
            <option value="classic">Team Deathmatch</option>
          </Form.Control>
        </Form.Group>

        {/* Create Match Button */}
        <Button variant="primary" onClick={handleCreateMatch} className="w-100">
          Create Match
        </Button>
      </Form>
    </div>
  );
};

export default Home;
