import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";
import { matchService } from "../services/matchService";

const Match = () => {
  const { matchId } = useParams(); // Get matchId from URL
  const navigate = useNavigate();
  const [matchData, setMatchData] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await matchService.getMatch(matchId);
        setMatchData(response);

        // Check if duration is 0 and update status to "end"
        if (response.duration === 0 && response.status !== "ended") {
          await matchService.endMatch(matchId); // Update status to "end"
          alert("Match has ended.");
        }
      } catch (error) {
        console.error("Error fetching match data:", error);
      }
    };

    fetchMatchDetails();

    // Auto-update duration every second
    const interval = setInterval(fetchMatchDetails, 1000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, [matchId]);

  const handleEndMatch = async () => {
    try {
      await matchService.endMatch(matchId);
      alert("Match has ended.");
      navigate("/"); // Redirect to home after match ends
    } catch (error) {
      console.error("Error ending match:", error);
    }
  };

  return (
    <div className="container mt-5 text-center">
      <h1>Match Name: {matchData?.gameMode}</h1>
      {matchData ? (
        <>
          <p>Remaining Time: {matchData.duration} seconds</p>
          <p>Game Mode: {matchData.gameMode}</p>
          <p>Status: {matchData.status}</p> {/* Display match status */}
          {matchData.status !== "ended" && (
            <Button variant="danger" onClick={handleEndMatch}>End Match</Button>
          )}
        </>
      ) : (
        <p>Loading match details...</p>
      )}
    </div>
  );
};

export default Match;
