import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Button, Form } from "react-bootstrap";
import { matchService } from "../services/matchService";

const Home = () => {
  const [duration, setDuration] = useState("");
  const navigate = useHistory();

  const handleCreateMatch = async () => {
    try {
      const response = await matchService.createMatch(duration);
      navigate.push(`/match/${response.matchId}`);  // Redirect to the match page
    } catch (error) {
      console.error("Error creating match:", error);
    }
  };

  return (
    <div className="text-center">
      <h1>Create a Time-Limited Match</h1>
      <Form.Group controlId="formDuration">
        <Form.Label>Match Duration (in seconds)</Form.Label>
        <Form.Control
          type="number"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
          placeholder="Enter match duration"
        />
      </Form.Group>
      <Button variant="primary" onClick={handleCreateMatch}>
        Create Match
      </Button>
    </div>
  );
};

export default Home;
