import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { socketService } from "../services/socketService";
import { Button } from "react-bootstrap";

const Match = () => {
  const { matchId } = useParams();
  const [remainingTime, setRemainingTime] = useState(null);
  const [matchStatus, setMatchStatus] = useState("");

  useEffect(() => {
    // Connect to WebSocket and join the match
    socketService.connectToMatch(matchId);

    // Listen for timer updates
    socketService.onTimerUpdate((data) => {
      setRemainingTime(data.remainingTime);
    });

    // Listen for match end notification
    socketService.onMatchEnd(() => {
      setMatchStatus("ended");
    });

    // Listen for warnings (e.g., 30 seconds left)
    socketService.onWarning((message) => {
      alert(message);
    });

    return () => {
      socketService.disconnect();
    };
  }, [matchId]);

  return (
    <div className="text-center">
      <h1>Match {matchId}</h1>
      {matchStatus !== "ended" ? (
        <>
          <h2>Remaining Time: {remainingTime} seconds</h2>
          {remainingTime <= 30 && <h3>Warning: Match ending soon!</h3>}
          <Button variant="danger" onClick={() => alert("Match Ended Manually")}>
            End Match
          </Button>
        </>
      ) : (
        <h2>Match has ended</h2>
      )}
    </div>
  );
};

export default Match;
