import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form } from "react-bootstrap";
import { matchService } from "../services/matchService";

const Home = () => {
  const [duration, setDuration] = useState(""); // Duration in seconds
  const [gameMode, setGameMode] = useState(""); // Game Mode
  const navigate = useNavigate();

  const handleCreateMatch = async () => {
    if (!duration || !gameMode) {
      alert("Please provide both match duration and game mode.");
      return;
    }

    try {
      const response = await matchService.createMatch({ duration, gameMode });
      navigate(`/match/${response.matchId}`); // Redirect to the match page with the matchId
    } catch (error) {
      console.error("Error creating match:", error);
    }
  };

  return (
    <div className="text-center">
      <h1>Create a Time-Limited Match</h1>
      
      <Form.Group controlId="formDuration">
        <Form.Label>Match Duration (in seconds)</Form.Label>
        <Form.Control
          type="number"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
          placeholder="Enter match duration"
        />
      </Form.Group>

      <Form.Group controlId="formGameMode">
        <Form.Label>Game Mode</Form.Label>
        <Form.Control
          as="select"
          value={gameMode}
          onChange={(e) => setGameMode(e.target.value)}
        >
          <option value="">Select Game Mode</option>
          <option value="timed">timed</option>
          <option value="capture-the-flag">Round Based</option>
          <option value="team-deathmatch">Classic</option>
        </Form.Control>
      </Form.Group>

      <Button variant="primary" onClick={handleCreateMatch}>
        Create Match
      </Button>
    </div>
  );
};

export default Home;
