import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Button } from "react-bootstrap";
import { matchService } from "../services/matchService";

const Match = () => {
  const { matchId } = useParams(); // Get matchId from URL
  const [matchData, setMatchData] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await matchService.getMatch(matchId);
        setMatchData(response);
      } catch (error) {
        console.error("Error fetching match data:", error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  return (
    <div className="container mt-5 text-center">
      <h1>Match ID: {matchId}</h1>
      {matchData ? (
        <>
          <p>Duration: {matchData.duration} seconds</p>
          <p>Game Mode: {matchData.gameMode}</p>
          <Button variant="danger">End Match</Button>
        </>
      ) : (
        <p>Loading match details...</p>
      )}
    </div>
  );
};

export default Match;
