import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Table } from "react-bootstrap";
import { matchService } from "../services/matchService";

const MatchList = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const data = await matchService.getAllMatches();
        setMatches(data);
      } catch (error) {
        console.error("Error fetching matches:", error);
      }
    };

    fetchMatches();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">All Matches</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Match ID</th>
            <th>Game Mode</th>
            <th>Duration</th>
            <th>End Time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {matches.length > 0 ? (
            matches.map((match) => (
              <tr key={match.matchId}>
                <td>{match.matchId}</td>
                <td>{match.gameMode}</td>
                <td></td>
                <td>{match.duration} sec</td>
                <td>{new Date(match.endTime).toLocaleTimeString()}</td>
                <td>
                  <Link to={`/match/${match.matchId}`} className="btn btn-primary">
                    View Match
                  </Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="text-center">No matches found</td>
            </tr>
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default MatchList;
